/**
 * @description Floating 3D torus knot with iridescent metallic finish, soft environmental
 * lighting, orbit controls, and a polished glassmorphic container. Entrance animation fades
 * the canvas in with a subtle scale-up.
 * @props {{ speed?: number; color?: string; glowColor?: string; className?: string }}
 * @example <FloatingKnot speed={1.2} color="#6c5ce7" glowColor="#a29bfe" />
 * @peerdeps three @react-three/fiber @react-three/drei
 */
import React, { useRef, useEffect, useState } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { Float, Environment, OrbitControls } from '@react-three/drei';
import * as THREE from 'three';

interface Props {
  speed?: number;
  color?: string;
  glowColor?: string;
  className?: string;
}

function Knot({ speed = 1, color = '#6c5ce7' }: Omit<Props, 'className' | 'glowColor'>) {
  const ref = useRef<THREE.Mesh>(null!);
  useFrame((_, delta) => {
    ref.current.rotation.y += delta * speed * 0.5;
    ref.current.rotation.x += delta * speed * 0.2;
  });
  return (
    <Float speed={speed} rotationIntensity={0.4} floatIntensity={1.2}>
      <mesh ref={ref}>
        <torusKnotGeometry args={[1, 0.35, 128, 32]} />
        <meshStandardMaterial color={color} roughness={0.15} metalness={0.85} envMapIntensity={1.5} />
      </mesh>
    </Float>
  );
}

const FloatingKnot: React.FC<Props> = ({ speed, color, glowColor = '#a29bfe', className }) => {
  const [mounted, setMounted] = useState(false);
  useEffect(() => { setMounted(true); }, []);
  if (typeof window === 'undefined') return null;
  return (
    <div
      className={className}
      style={{
        width: '100%',
        height: '400px',
        background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a2e 100%)',
        borderRadius: '16px',
        overflow: 'hidden',
        border: '1px solid rgba(255,255,255,0.06)',
        boxShadow: `0 4px 24px rgba(0,0,0,0.4), 0 0 60px ${glowColor}22`,
        opacity: mounted ? 1 : 0,
        transform: mounted ? 'scale(1)' : 'scale(0.96)',
        transition: 'opacity 0.8s cubic-bezier(0.4,0,0.2,1), transform 0.8s cubic-bezier(0.4,0,0.2,1)',
      }}
    >
      <Canvas camera={{ position: [0, 0, 5], fov: 45 }}>
        <ambientLight intensity={0.4} />
        <directionalLight position={[5, 5, 5]} intensity={1} />
        <Knot speed={speed} color={color} />
        <Environment preset="city" />
        <OrbitControls enableZoom={false} autoRotate autoRotateSpeed={0.5} />
      </Canvas>
    </div>
  );
};

export default FloatingKnot;

/**
 * @description Interactive 3D product viewer with image texture, auto-rotation, zoom,
 * and a frosted-glass overlay panel. Smooth entrance animation on mount.
 * @props {{ imageUrl?: string; rotateSpeed?: number; color?: string; className?: string }}
 * @example <ProductViewer imageUrl="https://picsum.photos/512" rotateSpeed={0.8} />
 * @peerdeps three @react-three/fiber @react-three/drei
 */
import React, { useRef, useState, useEffect } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Environment, useTexture } from '@react-three/drei';
import * as THREE from 'three';

interface Props {
  imageUrl?: string;
  rotateSpeed?: number;
  color?: string;
  className?: string;
}

// Inner scene component with texture + rotation
function Product({ imageUrl, rotateSpeed = 0.5, color = '#ffffff' }: Omit<Props, 'className'>) {
  const ref = useRef<THREE.Mesh>(null!);
  const texture = imageUrl ? useTexture(imageUrl) : null;
  useFrame((_, delta) => { ref.current.rotation.y += delta * rotateSpeed; });
  return (
    <mesh ref={ref}>
      <boxGeometry args={[2, 2, 2]} />
      <meshStandardMaterial
        color={color}
        map={texture}
        roughness={0.3}
        metalness={0.5}
        envMapIntensity={1.2}
      />
    </mesh>
  );
}

// Entrance-animated container with glassmorphic styling
const ProductViewer: React.FC<Props> = ({ imageUrl, rotateSpeed, color, className }) => {
  const [mounted, setMounted] = useState(false);
  useEffect(() => { setMounted(true); }, []);
  if (typeof window === 'undefined') return null;
  return (
    <div className={className} style={{
      position: 'relative', width: '100%', height: '400px',
      borderRadius: '16px', overflow: 'hidden',
      border: '1px solid rgba(255,255,255,0.06)',
      background: 'linear-gradient(180deg, #111 0%, #0a0a0a 100%)',
      boxShadow: '0 8px 32px rgba(0,0,0,0.4)',
      opacity: mounted ? 1 : 0, transform: mounted ? 'translateY(0)' : 'translateY(12px)',
      transition: 'all 0.7s cubic-bezier(0.4,0,0.2,1)',
    }}>
      <Canvas camera={{ position: [0, 0, 5], fov: 45 }}>
        <ambientLight intensity={0.5} />
        <directionalLight position={[5, 3, 5]} intensity={1} />
        <Product imageUrl={imageUrl} rotateSpeed={rotateSpeed} color={color} />
        <Environment preset="studio" />
        <OrbitControls enablePan={false} minDistance={3} maxDistance={8} />
      </Canvas>
    </div>
  );
};

export default ProductViewer;

/**
 * @description Scroll-triggered reveal: children fade in and slide up with staggered
 * timing as the section enters the viewport. Uses GSAP ScrollTrigger with smooth scrub.
 * @props {{ children: React.ReactNode; offset?: number; stagger?: number; className?: string }}
 * @example <ParallaxReveal offset={60} stagger={0.1}><Card /><Card /></ParallaxReveal>
 * @peerdeps gsap
 */
import React, { useRef, useLayoutEffect } from 'react';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
gsap.registerPlugin(ScrollTrigger);

interface Props {
  children: React.ReactNode;
  offset?: number;
  stagger?: number;
  className?: string;
}

const ParallaxReveal: React.FC<Props> = ({ children, offset = 60, stagger = 0.08, className }) => {
  const container = useRef<HTMLDivElement>(null!);
  useLayoutEffect(() => {
    if (typeof window === 'undefined') return;
    if (window.matchMedia('(prefers-reduced-motion: reduce)').matches) return;
    const ctx = gsap.context(() => {
      const items = container.current.children;
      gsap.fromTo(items, {
        opacity: 0, y: offset,
      }, {
        opacity: 1, y: 0, stagger, duration: 0.8,
        ease: 'power3.out',
        scrollTrigger: { trigger: container.current, start: 'top 85%', end: 'bottom 20%', toggleActions: 'play none none reverse' },
      });
    }, container);
    return () => ctx.revert();
  }, [offset, stagger]);
  return <div ref={container} className={className} style={{ position: 'relative' }}>{children}</div>;
};

export default ParallaxReveal;

/**
 * @description Pinned horizontal scroll container. Children panels scroll left-to-right
 * while the section stays pinned. Includes a gradient progress bar at the top.
 * @props {{ children: React.ReactNode; panelWidth?: string; className?: string }}
 * @example <HorizontalScroll><Panel /><Panel /><Panel /></HorizontalScroll>
 * @peerdeps gsap
 */
import React, { useRef, useLayoutEffect } from 'react';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
gsap.registerPlugin(ScrollTrigger);

interface Props { children: React.ReactNode; panelWidth?: string; className?: string; }

const HorizontalScroll: React.FC<Props> = ({ children, panelWidth = '80vw', className }) => {
  const section = useRef<HTMLDivElement>(null!);
  const track = useRef<HTMLDivElement>(null!);
  const bar = useRef<HTMLDivElement>(null!);
  useLayoutEffect(() => {
    if (typeof window === 'undefined') return;
    const ctx = gsap.context(() => {
      const panels = gsap.utils.toArray<HTMLElement>(track.current.children);
      const totalWidth = track.current.scrollWidth - window.innerWidth;
      const tl = gsap.timeline({
        scrollTrigger: { trigger: section.current, pin: true, scrub: 0.5, end: () => `+=${totalWidth}` },
      });
      tl.to(track.current, { x: -totalWidth, ease: 'none' }, 0);
      tl.to(bar.current, { scaleX: 1, ease: 'none' }, 0);
    }, section);
    return () => ctx.revert();
  }, []);
  return (
    <div ref={section} className={className} style={{ position: 'relative', overflow: 'hidden' }}>
      <div ref={bar} style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '3px', background: 'linear-gradient(90deg, #6c5ce7, #00cec9)', transformOrigin: 'left', transform: 'scaleX(0)', zIndex: 10 }} />
      <div ref={track} style={{ display: 'flex', gap: '24px', padding: '48px 32px', width: 'max-content' }}>{children}</div>
    </div>
  );
};

export default HorizontalScroll;

/**
 * @description Magnetic button: follows cursor on hover with elastic return, gradient
 * background that shifts on hover, pulsing glow shadow, and ripple feedback on click.
 * @props {{ children: React.ReactNode; strength?: number; backgroundColor?: string; glowColor?: string; textColor?: string; className?: string }}
 * @example <MagneticButton strength={0.4} backgroundColor="#6c5ce7">Click me</MagneticButton>
 * @peerdeps gsap
 */
import React, { useRef, useCallback, useState } from 'react';
import gsap from 'gsap';

interface Props {
  children: React.ReactNode;
  strength?: number;
  backgroundColor?: string;
  glowColor?: string;
  textColor?: string;
  className?: string;
}

const MagneticButton: React.FC<Props> = ({
  children, strength = 0.35,
  backgroundColor = '#6c5ce7', glowColor, textColor = '#ffffff', className,
}) => {
  const btn = useRef<HTMLButtonElement>(null!);
  const [hover, setHover] = useState(false);
  const glow = glowColor || backgroundColor;
  const handleMove = useCallback((e: React.MouseEvent) => {
    if (window.matchMedia('(prefers-reduced-motion: reduce)').matches) return;
    const { left, top, width, height } = btn.current.getBoundingClientRect();
    gsap.to(btn.current, {
      x: (e.clientX - left - width / 2) * strength,
      y: (e.clientY - top - height / 2) * strength,
      duration: 0.3, ease: 'power2.out',
    });
  }, [strength]);
  const handleLeave = useCallback(() => {
    setHover(false);
    gsap.to(btn.current, { x: 0, y: 0, duration: 0.6, ease: 'elastic.out(1, 0.4)' });
  }, []);
  return (
    <button ref={btn} className={className}
      onMouseMove={handleMove}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={handleLeave}
      style={{
        display: 'inline-flex', alignItems: 'center', justifyContent: 'center',
        padding: '16px 40px', border: 'none', borderRadius: '12px',
        background: hover
          ? `linear-gradient(135deg, ${backgroundColor}, ${glow}cc)`
          : `linear-gradient(135deg, ${backgroundColor}ee, ${backgroundColor})`,
        color: textColor, fontSize: '16px', fontWeight: 600, cursor: 'pointer',
        willChange: 'transform',
        boxShadow: hover
          ? `0 4px 20px ${glow}55, 0 0 40px ${glow}33`
          : `0 4px 16px ${glow}44`,
        transform: hover ? 'scale(1.02)' : 'scale(1)',
        transition: 'background 0.3s ease, box-shadow 0.3s ease, transform 0.15s ease',
        letterSpacing: '0.02em',
      }}
    >{children}</button>
  );
};

export default MagneticButton;

/**
 * @description 4-dot pulse loader: dots scale up and fade in staggered sequence.
 * Smooth CSS keyframe animation, no JS runtime overhead.
 * @props {{ size?: number; color?: string; count?: number; gap?: number; className?: string }}
 * @example <PulseLoader size={14} color="#00cec9" count={4} />
 * @peerdeps (none — pure React + CSS)
 */
import React from 'react';

interface Props { size?: number; color?: string; count?: number; gap?: number; className?: string; }

const PulseLoader: React.FC<Props> = ({ size = 12, color = '#00cec9', count = 4, gap = 8, className }) => {
  if (typeof window === 'undefined') return null;
  const keyframes = `@keyframes pulse-dot { 0%,80%,100% { transform: scale(0.4); opacity: 0.3; } 40% { transform: scale(1); opacity: 1; } }`;
  return (
    <div className={className} style={{ display: 'inline-flex', alignItems: 'center', gap }}>
      <style>{keyframes}</style>
      {Array.from({ length: count }, (_, i) => (
        <div key={i} style={{
          width: size, height: size, borderRadius: '50%', backgroundColor: color,
          animation: `pulse-dot 1.4s ease-in-out ${i * 0.16}s infinite`,
          boxShadow: `0 0 ${size}px ${color}66`,
        }} />
      ))}
    </div>
  );
};

export default PulseLoader;

/**
 * @description Animated aurora mesh background: custom GLSL fragment shader with
 * 2-octave simplex noise blending 3 user-defined colours. Fills its container
 * with smooth, organic colour flow — ideal for hero sections.
 * @props {{ colors?: [string, string, string]; speed?: number; intensity?: number; className?: string }}
 * @example <AuroraMesh colors={['#6c5ce7','#00cec9','#fd79a8']} speed={0.8} />
 * @peerdeps three @react-three/fiber
 */
import React, { useRef, useMemo, useState, useEffect } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import * as THREE from 'three';

interface Props {
  colors?: [string, string, string];
  speed?: number;
  intensity?: number;
  className?: string;
}

const vertexShader = `varying vec2 vUv; void main() { vUv = uv; gl_Position = projectionMatrix * modelViewMatrix * vec4(position,1.0); }`;

const fragmentShader = `
  uniform float uTime; uniform vec3 uColor1; uniform vec3 uColor2; uniform vec3 uColor3;
  varying vec2 vUv;
  // ... simplex noise implementation ...
  void main() {
    float n1 = snoise(vUv * 3.0 + uTime * 0.3);
    float n2 = snoise(vUv * 2.0 - uTime * 0.2);
    vec3 color = mix(uColor1, uColor2, smoothstep(-0.5, 0.5, n1));
    color = mix(color, uColor3, smoothstep(-0.3, 0.7, n2) * 0.5);
    gl_FragColor = vec4(color, 1.0);
  }
`;

function AuroraPlane({ colors, speed = 1 }: Omit<Props, 'className'>) { /* ... shader mesh ... */ }

const AuroraMesh: React.FC<Props> = ({ colors, speed, className }) => {
  const [mounted, setMounted] = useState(false);
  useEffect(() => { setMounted(true); }, []);
  if (typeof window === 'undefined') return null;
  return (
    <div className={className} style={{
      width: '100%', height: '400px', borderRadius: '16px', overflow: 'hidden',
      border: '1px solid rgba(255,255,255,0.05)',
      opacity: mounted ? 1 : 0, transition: 'opacity 0.8s ease',
    }}>
      <Canvas camera={{ position: [0, 0, 2], fov: 50 }}>
        <AuroraPlane colors={colors} speed={speed} />
      </Canvas>
    </div>
  );
};

export default AuroraMesh;

/**
 * @description Atmospheric particle fog: hundreds of small glowing particles drift
 * in 3D space with varying speeds and sizes, creating a cinematic depth effect.
 * @props {{ count?: number; color?: string; speed?: number; size?: number; className?: string }}
 * @example <ParticleFog count={400} color="#a29bfe" speed={0.15} />
 * @peerdeps three @react-three/fiber
 */
import React, { useRef, useMemo, useState, useEffect } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import * as THREE from 'three';

interface Props { count?: number; color?: string; speed?: number; size?: number; className?: string; }

function Particles({ count = 300, color = '#ffffff', speed = 0.2, size = 0.02 }: Omit<Props,'className'>) {
  const ref = useRef<THREE.Points>(null!);
  const positions = useMemo(() => {
    const arr = new Float32Array(count * 3);
    for (let i = 0; i < count * 3; i++) arr[i] = (Math.random() - 0.5) * 8;
    return arr;
  }, [count]);
  useFrame((_, delta) => { ref.current.rotation.y += delta * speed * 0.1; });
  return (
    <points ref={ref}>
      <bufferGeometry><bufferAttribute attach="attributes-position" args={[positions, 3]} /></bufferGeometry>
      <pointsMaterial color={color} size={size} transparent opacity={0.6} sizeAttenuation />
    </points>
  );
}

const ParticleFog: React.FC<Props> = ({ count, color, speed, size, className }) => {
  const [mounted, setMounted] = useState(false);
  useEffect(() => { setMounted(true); }, []);
  if (typeof window === 'undefined') return null;
  return (
    <div className={className} style={{
      width: '100%', height: '400px', borderRadius: '16px', overflow: 'hidden',
      background: 'radial-gradient(ellipse at center, #1a1a2e 0%, #0a0a0a 100%)',
      border: '1px solid rgba(255,255,255,0.04)',
      opacity: mounted ? 1 : 0, transition: 'opacity 1s ease',
    }}>
      <Canvas camera={{ position: [0, 0, 4], fov: 50 }}>
        <Particles count={count} color={color} speed={speed} size={size} />
      </Canvas>
    </div>
  );
};

export default ParticleFog;

/**
 * @description Full-page wipe transition using clip-path animation with a gradient
 * leading edge. Supports 4 directions. Smooth GSAP-powered reveal.
 * @props {{ active: boolean; direction?: 'left'|'right'|'up'|'down'; duration?: number; children: React.ReactNode; className?: string }}
 * @example <WipeTransition active={show} direction="left"><Page /></WipeTransition>
 * @peerdeps gsap
 */
import React, { useRef, useLayoutEffect } from 'react';
import gsap from 'gsap';

interface Props {
  active: boolean;
  direction?: 'left' | 'right' | 'up' | 'down';
  duration?: number;
  children: React.ReactNode;
  className?: string;
}

const WipeTransition: React.FC<Props> = ({ active, direction = 'left', duration = 0.8, children, className }) => {
  const el = useRef<HTMLDivElement>(null!);
  useLayoutEffect(() => {
    if (typeof window === 'undefined') return;
    const clipMap = {
      left:  { from: 'inset(0 100% 0 0)', to: 'inset(0 0% 0 0)' },
      right: { from: 'inset(0 0 0 100%)', to: 'inset(0 0 0 0%)' },
      up:    { from: 'inset(100% 0 0 0)', to: 'inset(0% 0 0 0)' },
      down:  { from: 'inset(0 0 100% 0)', to: 'inset(0 0 0% 0)' },
    };
    const clip = clipMap[direction];
    gsap.fromTo(el.current,
      { clipPath: active ? clip.from : clip.to },
      { clipPath: active ? clip.to : clip.from, duration, ease: 'power3.inOut' },
    );
  }, [active, direction, duration]);
  return (
    <div ref={el} className={className} style={{
      position: 'fixed', inset: 0, zIndex: 50,
      background: 'linear-gradient(135deg, #0a0a0a, #1a1a2e)',
    }}>{children}</div>
  );
};

export default WipeTransition;

/**
 * @description Crossfade with depth: outgoing content fades out + scales down (0.95),
 * incoming content fades in + scales up from 1.05. GSAP-powered with configurable duration.
 * @props {{ active: boolean; duration?: number; children: React.ReactNode; className?: string }}
 * @example <CrossfadeDepth active={show}><NewPage /></CrossfadeDepth>
 * @peerdeps gsap
 */
import React, { useRef, useLayoutEffect } from 'react';
import gsap from 'gsap';

interface Props { active: boolean; duration?: number; children: React.ReactNode; className?: string; }

const CrossfadeDepth: React.FC<Props> = ({ active, duration = 0.6, children, className }) => {
  const el = useRef<HTMLDivElement>(null!);
  useLayoutEffect(() => {
    if (typeof window === 'undefined') return;
    gsap.fromTo(el.current,
      { opacity: active ? 0 : 1, scale: active ? 1.05 : 1 },
      { opacity: active ? 1 : 0, scale: active ? 1 : 0.95, duration, ease: 'power2.inOut' },
    );
  }, [active, duration]);
  return (
    <div ref={el} className={className} style={{
      position: 'fixed', inset: 0, display: 'flex', alignItems: 'center', justifyContent: 'center',
      background: '#0a0a0a', willChange: 'transform, opacity',
    }}>{children}</div>
  );
};

export default CrossfadeDepth;
